	p = java.post(url, `id=${id}`, 
	{Referer: baseUrl}).body();
	d = JSON.parse(p).data;
dir = d.map($ => ({text: $.chaptername,
	href: baseUrl + $.chapterid+".html"}) );
	}catch(e){
		java.log(e)
		dir = [];
		}
list.concat(dir).reverse()

