host=parseInt(mhInfo.chapterId)>542724? host=host1:host=host2;
if (parseInt(mhInfo.chapterId)>885032)
host=host3;

header={"Referer":baseUrl,
"Accept":"image/webp,image/apng,image/*,*/*;q=0.8"};
headers={"headers":JSON.stringify(header)}
html='';
path=mhInfo.path;
for(i in mhInfo.images){
img=host+path+mhInfo.images[i];
html +='<img src="'+img+','+JSON.stringify(headers)+'">\n'
}
html

