function Login1() {
    let ck = cookie.getCookie(Get('url'));
    var URL = 'https://www.biliplus.com';
    java.webView('', URL + '/login', '');
    var json = java.ajax(URL + '/login?act=getauth');
    var url = String(JSON.parse(json).data.url).replace(/\&/g, '\u0026');
    java.startBrowser('https://www.coolapk.com/link?url=bilibili://browser/?url=' + url, '授权请求');
    var requestBody = String(url).replace(/.*\?(.*)&.*/, '$1');
    var headers = JSON.parse(cache.get(java.md5Encode16(Get('url') + "headers")).replace('"Referer":"' + Get('url'), '"Referer":"' + URL + '/login'));
    var option = {
        "method": "POST",
        "body": String(requestBody),
        "headers": headers
    };
    for (i = 0; i < 20; i++) {
        Response = java.ajax(URL + '/login?act=authpoll,' + JSON.stringify(option));
        var list = JSON.parse(Response);
        if (list.code == 0) {
            var mid = list.data.mid;
            var access_token = list.data.access_token;
            var refresh_token = list.data.refresh_token;
            var expire = list.data.cookie_info.cookies[0].expires;
            var url1 = URL + '/login?act=savekey&mid=' + mid + '&access_key=' + access_token + '&refresh_token=' + refresh_token + '&expire=' + expire;
            var res = java.webView('', url1, '');
            let ck1 = cookie.getCookie(Get('url'));
            cookie.replaceCookie(Get('url'), `${ck};${ck1}`);
            if (/success/.test(res)) {
                java.longToast('【登录提示】\n\n┏┅━┅━┅━┅━┅━┅━┅━┅━┅┓\n┋┋' + '\n❤️客户端授权已确认❤️\n┋┋' + '\n请返回阅读\n┋┋' + '\n┗┅━┅━┅━┅━┅━┅━┅━┅━┅┛');
                sleep(10000);
                java.startBrowser(URL + '/manga/', '登录成功');
                java.longToast('【登录提示】\n\n┏┅━┅━┅━┅━┅━┅━┅━┅━┅┓\n┋┋' + '\n❤️BiliPlus登录成功❤️\n┋┋' + '\n┗┅━┅━┅━┅━┅━┅━┅━┅━┅┛');
            } else {
                java.longToast('【登录提示】\n\n┏┅━┅━┅━┅━┅━┅━┅━┅━┅┓\n┋┋' + '\n♣️BiliPlus登录失败♣️\n┋┋' + '\n请重新登录\n┋┋' + '\n┗┅━┅━┅━┅━┅━┅━┅━┅━┅┛');
            }
            break;
        }
        if (list.code !== 0 && i == 19) {
            cookie.setCookie(Get('url'), `${ck}`);
            cookie.removeCookie(URL);
            java.longToast('【登录提示】\n\n┏┅━┅━┅━┅━┅━┅━┅━┅━┅┓\n┋┋' + '\n♣️BiliPlus授权未确认♣️\n┋┋' + '\n┗┅━┅━┅━┅━┅━┅━┅━┅━┅┛');
        }
        sleep(1000);
    }
}

