function get(tag,num) {
    const { java, source, cookie, cache } = this;
    var get = JSON.parse(source.getVariable());
    var Url = get['url'].replace(/www/, 'v4api') + '/app/v1/comic/filter/classify';
    var list = JSON.parse(java.ajax(Url)).data.classifyList;
    if (/theme/.test(tag)) {
        theme = list[0].list[num].tagId;
    }
    if (/cate/.test(tag)) {
        cate = list[1].list[num].tagId;
    }
    if (/status/.test(tag)) {
        status = list[2].list[num].tagId;
    }
    if (/zone/.test(tag)) {
        zone = list[3].list[num].tagId;
    }
    var e = eval(tag);
    return e;
}
