}
if (/cpx=/.test(src)) {
    let cpx = src.match(/cpx=(.+?)&token/)[1];
    let iv = java.base64Decode(decodeURIComponent(cpx)).substring(60, 76);
    let data_length = parseInt(java.bytesToStr(result.slice(1, 5)));
    let key = result.slice(data_length + 5);
    let content = result.slice(5, data_length + 5);
    let head = decode(content.slice(0, 20496), key, iv);
    result = Buffer.concat([head, content.slice(20496)]);
    result;
} else {
    result;
}

